package com.clough.cat_project;

public class BritishShortHair extends Cat {

	
		private String name;
		private String gender;
		private int age;
		private double weight;
		private boolean isAMouser;
		
	public BritishShortHair(String name, String gender, int age, double weight, boolean isAMouser) {
		// TODO Auto-generated constructor stub
		super(name, gender, age, weight, isAMouser);
		this.name = name;
		this.gender = gender;
		this.age = age;
		this.weight = weight;
		this.isAMouser = isAMouser;
		
	}

}
